#!/bin/sh

if [ -z "${CONNECT_TIME}" ]; then
	case "${PPP_IFACE}" in
		"ppp0")
			ip -6 route add fda0:7a62:1::/64 dev ${PPP_IFACE}
			;;
		"ppp1")
			ip -6 route add fda0:7a62:2::/64 dev ${PPP_IFACE}
			;;
	esac
fi

# cat /etc/ppp/ipv6-up.d/defaultroute
#!/bin/sh -e

if [ -z "${CONNECT_TIME}" ]; then
        ip -6 route add default dev ${PPP_IFACE}
fi
